import { Typography, Box, Button, Grid, 
    Container, Paper, TextField, Divider } from '@mui/material';
import React from 'react';

const EditPassword = () => {
    return (
        <React.Fragment>

        <Paper variant="outlined" sx={{ my: { xs: 3, md: 6}, p: { xs: 2, md: 3 } }}>
            <Grid container spacing={3}>
            <Grid item xs={12} md={12} lg={12}>
            <Typography
                    // variant="h5"
                    sx={{ textAlign: 'center', textDecoration: 'underline'}}
                >
                Change Password
            </Typography>
            </Grid>

            <Divider sx={{ pt: 3}}/>
                <Grid item xs={12} md={6} lg={4}>
            <Typography sx={{ pt: 3}}>
                Old Password:
            </Typography>
            </Grid>
            <Grid item xs={12} md={6} lg={8}>
                <TextField
                        required
                        fullWidth
                        variant="outlined"
                        //value={props.form.fname}
                        //onChange={(e) => props.onChange([props.form.fname = e.target.value])}
                    />
                </Grid>
                <Grid item xs={12} md={6} lg={4}>
            <Typography sx={{ pt: 3}}>
                New Password:
            </Typography>
            </Grid>
            <Grid item xs={12} md={6} lg={8}>
                <TextField
                        required
                        fullWidth
                        variant="outlined"
                        //value={props.form.fname}
                        //onChange={(e) => props.onChange([props.form.fname = e.target.value])}
                    />
                </Grid>
            <Grid item xs={12} md={6} lg={4}>
            <Typography sx={{ pt: 3}}>
                Confirm Password:
            </Typography>
            </Grid>
            <Grid item xs={12} md={6} lg={8}>
                <TextField
                        required
                        fullWidth
                        variant="outlined"
                        //value={props.form.fname}
                        //onChange={(e) => props.onChange([props.form.fname = e.target.value])}
                    />
                </Grid>
                </Grid>

            <Divider sx={{ pt: 3}}/>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', my: 4 }}>
                <Button
                    //type="submit"
                    variant="contained"
                    //onClick={handleNext}
                >
                    Submit
                </Button>
            </Box>
            </Paper>
        </React.Fragment>
    );
};

export default EditPassword;
